import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { login } from '@store/authentification/auth.slice';
import { Row, Col, Typography, Alert } from 'antd';
import LoginForm from '../LoginForm/Login.form';
import { selectorAuth } from '@store/authentification/auth.slice';

const LoginPage = () => {
  const { Title } = Typography;
  const dispatch = useDispatch();
  const auth = useSelector(selectorAuth);
  const handleSubmit = values => {
    dispatch(login({ email: values.email, password: values.password }));
  };

  return (
    <>
      <Row justify='center'>
        <Col span={8}>
          <Row>
            <Col span={12}>
              <Title>Login</Title>
            </Col>
            <Col span={12}>Register</Col>
          </Row>
          <LoginForm onSubmit={handleSubmit} isLoading={auth.isLoading} />
          {auth.error && <Alert message='Error' description={auth.error.message} type='error' showIcon />}
        </Col>
      </Row>
    </>
  );
};

export default LoginPage;
